/* Font Styles */
$regular: 400;
$bold: 700;
$semi-bold: 600;
$h-family: 'Poppins';
$p-family: 'Open Sans';

/* Colors */
$white: hsl(0, 0%, 100%);
$pink: hsl(322, 100%, 66%);
$very-pale-cyan: hsl(193, 100%, 96%);
$very-dark-cyan: hsl(192, 100%, 9%);
$grayish-blue: hsl(208, 11%, 55%);

/* Button */ 
$buttonPadding: 13px 60px;

